package dec21Assignment;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0;i<=5;i++)
			{	
				int k=0;
					for(int j=1;j<=i;j++)
					{ 
						k++;
						System.out.print(k);
					}
				k++;
				System.out.println(k);
			}
		
	}
}
