package string;

public class ArrayTOStringLogical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        String arr[]= {"hi","Friend"};
        String ch="";
       
        for(int i=0;i<arr.length;i++)
        {
        	ch=ch+arr[i];
        	
        }
     System.out.println(ch);
	}

}
