package string;

public class StringTOArrayLogical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 String s="hello";
		 char ch[]=new char[5];
		 for(int i=0;i<s.length();i++)
		 {
			 ch[i]=s.charAt(i);
			 System.out.println(ch[i]);
			 
		 }
	}

}
